import{productArray}from"./constants/productList.js";const queryString=document.location.search,params=new URLSearchParams(queryString),id=params.get("id"),productsContainer=document.querySelector(".products"),cart=document.querySelector(".cart"),cartList=document.querySelector(".cart-list"),totalContainer=document.querySelector(".total");let cartArray=[];productArray.forEach((function(t){productsContainer.innerHTML+=`\n  <div class="product">\n    <h2>${t.name}</h2>\n      <p>${t.description}</p>\n    <div style="background-image: url(${t.image})" class="product-image"></div>\n    <div class="product-price">${t.price}</div>\n    <button class="product-button" data-product="${t.id}">Add to cart</button>\n   </div>`}));const buttons=document.querySelectorAll("button");function showCart(t){cart.style.display="flex",cartList.innerHTML=" ";let r=0;t.forEach((function(t){r+=t.price,cartList.innerHTML+=`\n            <div class="cart-item">\n            <h4>${t.name}</h4>\n            <div style="background-image: url(${t.image})" class="cart-image"></div>\n            </div>`})),totalContainer.innerHTML=`Total: ${r}`}buttons.forEach((function(t){t.onclick=function(t){const r=productArray.find((r=>r.id===t.target.dataset.product));cartArray.push(r),showCart(cartArray),localStorage.setItem("cartList",JSON.stringify(cartArray))}}));
const form=document.querySelector("#contactForm"),firstName=document.querySelector("#firstName"),firstNameError=document.querySelector("#firstNameError"),lastName=document.querySelector("#lastName"),lastNameError=document.querySelector("#lastNameError"),subject=document.querySelector("#subject"),subjectError=document.querySelector("#subjectError"),email=document.querySelector("#email"),emailError=document.querySelector("#emailError"),address=document.querySelector("#address"),addressError=document.querySelector("#addressError");function validateForm(e){e.preventDefault(),!0===checkLength(firstName.value,2)?firstNameError.style.display="none":firstNameError.style.display="block",!0===checkLength(lastName.value,2)?lastNameError.style.display="none":lastNameError.style.display="block",!0===checkLength(subject.value,10)?subjectError.style.display="none":subjectError.style.display="block",!0===validateEmail(email.value)?emailError.style.display="none":emailError.style.display="block",!0===checkLength(address.value,25)?addressError.style.display="none":addressError.style.display="block"}function checkLength(e,r){return e.trim().length>r}function validateEmail(e){return/\S+@\S+\.\S+/.test(e)}form.addEventListener("submit",validateForm);
